  const [muscles,setMuscles] = useState([]);

  function addMuscles(name) {
    if (muscles.includes(name)){
      setMuscles(muscles.filter((muscle)=> muscle !== name));
    }
    else {
      setMuscles([...muscles,name]);
    }
  }

